import React, {useState} from 'react';

export default function Query(){


    const [queryState, updateQueryState] = useState('')
    const [results, updateResults] = useState('')
    const [verb, updateVerb] = useState('create')
    function handleQueryChange(e){
        updateQueryState(e.target.value)
       
    }

    function handleSubmit(){
        console.log(`[-] ${queryState}`)
        updateResults(queryState)
    }

    function handleVerbChange(verb){
        console.log(`[-] Verb: ${verb}`)
        updateVerb(verb)
    }
    return(
        <>
            <h1>Query</h1>
            <select name="Verb" id="cars" onChange={(e) => handleVerbChange(e.target.value)}>
                <option value="create">CREATE</option>
                <option value="read">READ</option>
                <option value="update">UPDATE</option>
                <option value="delete">DELETE</option>
            </select>
            <textarea onChange={(e) => handleQueryChange(e)}/><br/>
            <button onClick={() => handleSubmit()}>Submit</button>
            <h1> Results</h1>
            {results === '' ? 
                
                <p>No results found</p>
                :
                <p>[-] {results}</p>
            }
        </>
    );
}
